    var listdata = [];
    var tempresult = null;
    var tempdata = null;
    for (var i = 0; i < query.length; i++) {
      tempdata = query[i];
      if (tempdata.apsara == true) {
        listdata.push(tempdata.apsaraId);
      }
      else {
        listdata.push(undefined);
      }
    }

    //console.log(listdata);
