  async countReason(postID: string) {
    let query = await this.loaddata.aggregate([
      {
        $match: {

          postID: postID
        }
      },
      {
        $unwind: "$reportedUser"
      },
      {
        $match: {

          'reportedUser.active': true
        }
      },
      {
        $group: {
          _id: "$reportedUser.description",

          myCount: {
            $sum: 1
          }
        }
      },
      {
        $project: {
          _id: "$_id",
          "myCount": "$myCount",

        }
      }

    ]);
    return query;
  }

