                    "isShared": 1,
                    "likes": 1,
                    "views": 1,
                    "shares": 1,
                    "comments": 1,
                    "insight": 1,
                    "userProfile": 1,
                    "contentMedias": 1,
                    "cats": "$cats",
                    "tagDescription": 1,
                    "metadata": 1,
                    "boostDate": 1,
                    "end": 1,
                    "start": 1,
                    "isBoost": 1,
                    "boostViewer": 1,
